package com.example.ftm;

import androidx.appcompat.app.AppCompatActivity;

import android.content.Intent;
import android.os.Bundle;
import android.view.View;
import android.widget.ImageView;
import android.widget.Toast;

import com.smarteist.autoimageslider.DefaultSliderView;
import com.smarteist.autoimageslider.IndicatorAnimations;
import com.smarteist.autoimageslider.SliderLayout;

public class ConsumerHome extends AppCompatActivity {

    SliderLayout sliderLayout;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_consumer_home);
        sliderLayout = findViewById(R.id.imageSlider);
        sliderLayout.setIndicatorAnimation(IndicatorAnimations.FILL); //set indicator animation by using SliderLayout.Animations. :WORM or THIN_WORM or COLOR or DROP or FILL or NONE or SCALE or SCALE_DOWN or SLIDE and SWAP!!
        sliderLayout.setScrollTimeInSec(2); //set scroll delay in seconds :

        setSliderViews();
    }
    public void openActivity(View v){
        Toast.makeText(this, "Tomatoes", Toast.LENGTH_SHORT).show();
        Intent intent = new Intent(this,Tomato.class);
        startActivity(intent);
    }
    private void setSliderViews() {

        for (int i = 0; i <= 4; i++) {

            DefaultSliderView sliderView = new DefaultSliderView(this);

            switch (i) {
                case 0:
                    //sliderView.setImageUrl("data:image/jpeg;base64,");
                    //sliderView.setImageUrl("https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.dreamstime.com%2Fbeautiful-farmland-rural-landscape-cypress-trees-colorful-spring-flowers-tuscany-italy-typical-house-amazing-image143034218&psig=AOvVaw13ypUY8RCQcaheoXCgvwG4&ust=1638452020287000&source=images&cd=vfe&ved=0CAsQjRxqFwoTCOiChcrbwvQCFQAAAAAdAAAAABAV");
                    sliderView.setImageUrl("https://i.snipboard.io/4eGCbQ.jpg");
                    break;
                case 1:
                    //sliderView.setImageUrl("data:image/jpeg;base64,");
                    sliderView.setImageUrl("https://i.snipboard.io/UMCz5i.jpg");
                    break;
                case 2:
                    //sliderView.setImageUrl("data:image/jpeg;base64,");
                    sliderView.setImageUrl("https://i.snipboard.io/OsBcfv.jpg");
                    break;

                case 3:
                    //sliderView.setImageUrl("https://static.wikia.nocookie.net/snl/images/f/f1/Scarlett-johansson.jpg/revision/latest?cb=20210828013135");
                    sliderView.setImageUrl("https://i.snipboard.io/k8CsuJ.jpg");
                    break;
                case 4:
                    sliderView.setImageUrl("https://cdn.vox-cdn.com/thumbor/dVsnOnMizyCQMUGAT6Vb6WBiByY=/1400x1400/filters:format(jpeg)/cdn.vox-cdn.com/uploads/chorus_asset/file/18314696/1141751041.jpg.jpg");
                    break;
            }

            sliderView.setImageScaleType(
                    ImageView.ScaleType.CENTER_CROP);
            //at last add this view in your layout :
            sliderLayout.addSliderView(sliderView);

        }
    }
}